targets.expr:
sum(          avg_over_time(
            gitlab_service_ops:rate{type="$type"}[4h] offset 166h # 1 week - 2 hours
          )
          + gitlab_service_ops:rate:avg_over_time_1w - gitlab_service_ops:rate:avg_over_time_1w offset 1w
)

sum(
avg_over_time(
            gitlab_service_ops:rate{type="$type"}[4h] offset 334h # 2 weeks - 2 hours
          )
          + gitlab_service_ops:rate:avg_over_time_1w{type="$type", environment="gprd"} - gitlab_service_ops:rate:avg_over_time_1w{type="$type", environment="gprd"} offset 2w
          )

sum(          avg_over_time(
            gitlab_service_ops:rate{type="$type", environment="gprd"}[4h] offset 502h # 3 weeks - 2 hours
          )
          + gitlab_service_ops:rate:avg_over_time_1w{type="$type", environment="gprd"} - gitlab_service_ops:rate:avg_over_time_1w{type="$type", environment="gprd"} offset 3w
)
