targets.expr:
min(
  min_over_time(
    gitlab_service_apdex:ratio{environment="$environment", type="frontend", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  max_over_time(
    gitlab_service_errors:ratio{environment="$environment", type="frontend", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  avg_over_time(
    gitlab_service_ops:rate{environment="$environment", type="frontend", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

min(
  min_over_time(
    gitlab_service_apdex:ratio{environment="$environment", type="frontend", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  max_over_time(
    gitlab_service_errors:ratio{environment="$environment", type="frontend", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  avg_over_time(
    gitlab_service_ops:rate{environment="$environment", type="frontend", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

sum(
  rate(
    namedprocess_namegroup_cpu_seconds_total{
      environment="$environment",
      groupname="haproxy",
      type="frontend",
      stage="$stage"
    }[$__interval]
  )
) without (mode)

max(
    namedprocess_namegroup_open_filedesc{
      environment="$environment",
      groupname="haproxy",
      type="frontend",
      stage="$stage"
    }
) by (fqdn)

sum(
    namedprocess_namegroup_num_threads{
      environment="$environment",
      groupname="haproxy",
      type="frontend",
      stage="$stage"
    }
) by (fqdn)

sum(
    namedprocess_namegroup_memory_bytes{
      environment="$environment",
      groupname="haproxy",
      type="frontend",
      stage="$stage"
    }
) by (fqdn)

clamp_min(clamp_max(1 -
(
  (
    node_memory_MemFree_bytes{environment="$environment", stage=~"|$stage", type="frontend"} +
    node_memory_Buffers_bytes{environment="$environment", stage=~"|$stage", type="frontend"} +
    node_memory_Cached_bytes{environment="$environment", stage=~"|$stage", type="frontend"}
  )
)
/
node_memory_MemTotal_bytes{environment="$environment", stage=~"|$stage", type="frontend"}
,1),0)

clamp_min(
  clamp_max(
    max(
  (
    (
      node_filesystem_size_bytes{environment="$environment", type="frontend", stage="$stage", fstype=~"ext.|xfs"}
      -
      node_filesystem_free_bytes{environment="$environment", type="frontend", stage="$stage", fstype=~"ext.|xfs"}
    )
    /
    node_filesystem_size_bytes{environment="$environment", type="frontend", stage="$stage", fstype=~"ext.|xfs"}
  )
) by (device, fqdn)

  ,1)
,0)

clamp_min(
  clamp_max(
    max(
  label_replace(
    process_open_fds{environment="$environment", type="frontend", stage="$stage"}
    /
    process_max_fds{environment="$environment", type="frontend", stage="$stage"}
    , "client", "general", "", ""
  )
  or
  label_replace(
    ruby_file_descriptors{environment="$environment", type="frontend", stage="$stage"}
    /
    ruby_process_max_fds{environment="$environment", type="frontend", stage="$stage"}
    , "client", "ruby", "", ""
  )
) by (job, instance)

  ,1)
,0)

max by (type, stage, component) (
  clamp_max(
    gitlab_component_saturation:ratio{environment="$environment", type="frontend", stage=~"|$stage"}
    ,
    1
  ) >= on(component, monitor, env) group_left slo:max:soft:gitlab_component_saturation:ratio
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:avg_over_time_1w{
        environment="$environment",
        type="frontend", stage=~"|$stage"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="frontend", stage=~"|$stage"
        }
      , 1
    )
    >= on(component, monitor, env) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:predict_linear_2w{
        environment="$environment",
        type="frontend", stage=~"|$stage"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="frontend", stage=~"|$stage"
        }
    , 1
    )
    >= on(component, monitor, env) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

clamp_min(clamp_max(
  max(
    gitlab_component_saturation:ratio{
      type="frontend",
      environment="$environment",
      stage=~"|$stage"
    }
  ) by (component)
  ,1)
,0)
