targets.expr:
clamp_min(
  avg by (type) (
    gitlab_service_ops:rate:prediction{env="$environment",environment="$environment",monitor="global",stage="$stage",type="frontend"}
    + (2) *
    gitlab_service_ops:rate:stddev_over_time_1w{env="$environment",environment="$environment",monitor="global",stage="$stage",type="frontend"}
  )
  or
  (
      sum by (type) (gitlab_service_ops:rate:prediction{environment="$environment",monitor!~"global|",stage="$stage",type="frontend"})
      + (2) *
      sum by (type) (gitlab_service_ops:rate:stddev_over_time_1w{environment="$environment",monitor!~"global|",stage="$stage",type="frontend"})
  ),
  0
)

clamp_min(
  avg by (type) (
    gitlab_service_ops:rate:prediction{env="$environment",environment="$environment",monitor="global",stage="$stage",type="frontend"}
    + (-2) *
    gitlab_service_ops:rate:stddev_over_time_1w{env="$environment",environment="$environment",monitor="global",stage="$stage",type="frontend"}
  )
  or
  (
      sum by (type) (gitlab_service_ops:rate:prediction{environment="$environment",monitor!~"global|",stage="$stage",type="frontend"})
      + (-2) *
      sum by (type) (gitlab_service_ops:rate:stddev_over_time_1w{environment="$environment",monitor!~"global|",stage="$stage",type="frontend"})
  ),
  0
)

histogram_quantile(
  0.950000,
  sum by (le) (
    rate(haproxy_http_response_duration_seconds_bucket{type="frontend", backend_name!~"canary_.*", backend_name!="api_rate_limit", backend_name!="websockets", environment="$environment"}[$__interval])
  )
)

histogram_quantile(
  0.950000,
  sum by (fqdn,le) (
    rate(haproxy_http_response_duration_seconds_bucket{type="frontend", backend_name!~"canary_.*", backend_name!="api_rate_limit", backend_name!="websockets", environment="$environment"}[$__interval])
  )
)

clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{environment="$environment",stage="$stage",type="frontend"}
        /
        process_max_fds{environment="$environment",stage="$stage",type="frontend"}
      )
      or
      (
        ruby_file_descriptors{environment="$environment",stage="$stage",type="frontend"}
        /
        ruby_process_max_fds{environment="$environment",stage="$stage",type="frontend"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)

clamp_min(clamp_max(
  max(
    gitlab_component_saturation:ratio{
      type="frontend",
      environment="$environment",
      stage=~"|$stage"
    }
  ) by (component)
  ,1)
,0)
