clamp_min(
  avg by (type) (
    gitlab_service_ops:rate:prediction{env="$environment",environment="$environment",monitor="global",stage="main",type="web"}
    + (2) *
    gitlab_service_ops:rate:stddev_over_time_1w{env="$environment",environment="$environment",monitor="global",stage="main",type="web"}
  )
  or
  (
      sum by (type) (gitlab_service_ops:rate:prediction{environment="$environment",monitor!~"global|",stage="main",type="web"})
      + (2) *
      sum by (type) (gitlab_service_ops:rate:stddev_over_time_1w{environment="$environment",monitor!~"global|",stage="main",type="web"})
  ),
  0
)

