targets.expr:
sort(
  max(
  ALERTS{environment="$environment", type="$type", stage="$stage", severity="critical", alertstate="firing"} * 3
  or
  ALERTS{environment="$environment", type="$type", stage="$stage", severity="error", alertstate="firing"} * 2
  or
  ALERTS{environment="$environment", type="$type", stage="$stage", severity="warn", alertstate="firing"}
  ) by (alertname, severity)
)

min(
  min_over_time(
    gitlab_service_apdex:ratio{environment="$environment", type="$type", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  max_over_time(
    gitlab_service_errors:ratio{environment="$environment", type="$type", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

min(
  min_over_time(
    gitlab_service_availability:ratio{environment="$environment", type="$type", stage="$stage"}[$__interval]
  )
) by (tier, type)

min(
  min_over_time(
    gitlab_service_availability:ratio{environment="$environment", type="$type", stage=""}[$__interval]
  )
) by (tier, type)

min(
  min_over_time(
    gitlab_service_availability:ratio{environment="$environment", type="$type", stage="$stage"}[$__interval] offset 1w
  )
) by (tier, type)

max(
  avg_over_time(
    gitlab_service_ops:rate{environment="$environment", type="$type", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  max_over_time(
    gitlab_service_saturation:ratio{environment="$environment", type="$type", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

clamp_min(clamp_max(
        1 -
        (
          (
            node_memory_MemFree_bytes{environment="$environment", stage=~"|$stage", type="$type"} +
            node_memory_Buffers_bytes{environment="$environment", stage=~"|$stage", type="$type"} +
            node_memory_Cached_bytes{environment="$environment", stage=~"|$stage", type="$type"}
          )
        )
        /
        node_memory_MemTotal_bytes{environment="$environment", stage=~"|$stage", type="$type"}
      ,1),0)

sort(
          clamp_min(
            clamp_max(
              max(
                gitlab_component_saturation:ratio:avg_over_time_1w{environment="$environment", type="$type", stage=~"|$stage"}
                + 2 * gitlab_component_saturation:ratio:stddev_over_time_1w{environment="$environment", type="$type", stage=~"|$stage"}
              ) by (component),
            1),
          0)
        )

sort(
          clamp_min(
            clamp_max(
              max(
                gitlab_component_saturation:ratio:predict_linear_2w{environment="$environment", type="$type", stage=~"|$stage"}
                + 2 * gitlab_component_saturation:ratio:stddev_over_time_1w{environment="$environment", type="$type", stage=~"|$stage"}
              ) by (component),
            1),
          0)
        )

clamp_min(clamp_max(
          max(
            gitlab_component_saturation:ratio{
              type="$type",
              environment="$environment",
              stage=~"|$stage"
            }
          ) by (component)
          ,1),0)

clamp_min(
          clamp_max(
            max(
              gitlab_component_saturation:ratio:avg_over_time_1w{
                type="$type",
                environment="$environment",
                stage=~"$stage|"
              }
            ) by (component)
          ,1),0)
