targets.expr:
clamp_min(
  avg by (type) (
    gitlab_service_ops:rate:prediction{env="$environment",environment="$environment",monitor="global",stage="$stage",type="$type"}
    + (2) *
    gitlab_service_ops:rate:stddev_over_time_1w{env="$environment",environment="$environment",monitor="global",stage="$stage",type="$type"}
  )
  or
  (
      sum by (type) (gitlab_service_ops:rate:prediction{environment="$environment",monitor!~"global|",stage="$stage",type="$type"})
      + (2) *
      sum by (type) (gitlab_service_ops:rate:stddev_over_time_1w{environment="$environment",monitor!~"global|",stage="$stage",type="$type"})
  ),
  0
)

clamp_min(
  avg by (type) (
    gitlab_service_ops:rate:prediction{env="$environment",environment="$environment",monitor="global",stage="$stage",type="$type"}
    + (-2) *
    gitlab_service_ops:rate:stddev_over_time_1w{env="$environment",environment="$environment",monitor="global",stage="$stage",type="$type"}
  )
  or
  (
      sum by (type) (gitlab_service_ops:rate:prediction{environment="$environment",monitor!~"global|",stage="$stage",type="$type"})
      + (-2) *
      sum by (type) (gitlab_service_ops:rate:stddev_over_time_1w{environment="$environment",monitor!~"global|",stage="$stage",type="$type"})
  ),
  0
)

clamp_min(clamp_max(
  max(
    gitlab_component_saturation:ratio{
      type="$type",
      environment="$environment",
      stage=~"|$stage"
    }
  ) by (component)
  ,1)
,0)
