targets.expr:
sum by (environment, env, tier, type, stage, fqdn) (
  gitlab_component_node_ops:rate_5m{env="$environment",environment="$environment",fqdn=~"$fqdn",monitor!~"global|",type="gitaly"}
)

clamp_min(clamp_max(sum(
  rate(
    gitaly_rate_limiting_acquiring_seconds_bucket{
      fqdn=~"$fqdn",
      le="60"
    }[$__interval]
  )
) by (environment, tier, type, stage, fqdn, grpc_method)
/
sum(
  rate(
    gitaly_rate_limiting_acquiring_seconds_bucket{
      fqdn=~"$fqdn",
      le="+Inf"
    }[$__interval]
  )
) by (environment, tier, type, stage, fqdn, grpc_method)
,1),0)

clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{fqdn=~"$fqdn"}
        /
        process_max_fds{fqdn=~"$fqdn"}
      )
      or
      (
        ruby_file_descriptors{fqdn=~"$fqdn"}
        /
        ruby_process_max_fds{fqdn=~"$fqdn"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)

histogram_quantile(
  0.950000,
  sum by (["le"]) (
    rate(grpc_server_handling_seconds_bucket{fqdn=~"$fqdn"}[$__interval]) and on(grpc_service,grpc_method) grpc_client_handled_total{job="gitaly"}
  )
)
