targets.expr:
sum(
  rate(
    gitaly_rate_limiting_acquiring_seconds_bucket{
      environment="$environment",fqdn=~"$fqdn",
      le="60"
    }[$__interval]
  )
) by (environment, tier, type, stage, fqdn, grpc_method)
/
sum(
  rate(
    gitaly_rate_limiting_acquiring_seconds_bucket{
      environment="$environment",fqdn=~"$fqdn",
      le="+Inf"
    }[$__interval]
  )
) by (environment, tier, type, stage, fqdn, grpc_method)

clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{environment="$environment",fqdn=~"$fqdn"}
        /
        process_max_fds{environment="$environment",fqdn=~"$fqdn"}
      )
      or
      (
        ruby_file_descriptors{environment="$environment",fqdn=~"$fqdn"}
        /
        ruby_process_max_fds{environment="$environment",fqdn=~"$fqdn"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)

histogram_quantile(
  0.950000,
  sum by (["le"]) (
    rate(grpc_server_handling_seconds_bucket{environment="$environment",fqdn=~"$fqdn"}[$__interval]) and on(grpc_service,grpc_method) grpc_client_handled_total{job="gitaly"}
  )
)
