targets.expr:
clamp_min(
  avg by (type) (
    gitlab_service_ops:rate:prediction{env="$environment", environment="$environment", monitor="global", stage="$stage", type="gitaly"}
    + (2) *
    gitlab_service_ops:rate:stddev_over_time_1w{env="$environment", environment="$environment", monitor="global", stage="$stage", type="gitaly"}
  )
  or
  (
      sum by (type) (gitlab_service_ops:rate:prediction{environment="$environment", monitor!~"global|", stage="$stage", type="gitaly"})
      + (2) *
      sum by (type) (gitlab_service_ops:rate:stddev_over_time_1w{environment="$environment", monitor!~"global|", stage="$stage", type="gitaly"})
  ),
  0
)

clamp_min(
  avg by (type) (
    gitlab_service_ops:rate:prediction{env="$environment", environment="$environment", monitor="global", stage="$stage", type="gitaly"}
    + (-2) *
    gitlab_service_ops:rate:stddev_over_time_1w{env="$environment", environment="$environment", monitor="global", stage="$stage", type="gitaly"}
  )
  or
  (
      sum by (type) (gitlab_service_ops:rate:prediction{environment="$environment", monitor!~"global|", stage="$stage", type="gitaly"})
      + (-2) *
      sum by (type) (gitlab_service_ops:rate:stddev_over_time_1w{environment="$environment", monitor!~"global|", stage="$stage", type="gitaly"})
  ),
  0
)

max(
  max_over_time(
    gitlab_component_saturation:ratio{environment="$environment", type="gitaly", stage="$stage"}[$__interval]
  )
) by (component)

sum(
  gitlab_component_errors:rate{component="goserver", environment="$environment", monitor!~"global|", stage="$stage", type="gitaly"}
) by (component)
/
sum(
  gitlab_component_ops:rate{component="goserver", environment="$environment", monitor!~"global|", stage="$stage", type="gitaly"}
) by (component)

sum(
  avg_over_time(
    gitlab_component_ops:rate{component="goserver", environment="$environment", monitor!~"global|", stage="$stage", type="gitaly"}[$__interval]
  )
) by (component)

histogram_quantile(
  0.950000,
  sum by (le) (
    rate(grpc_server_handling_seconds_bucket{environment="$environment", stage="$stage"}[$__interval]) and on(grpc_service,grpc_method) grpc_client_handled_total{job="gitaly"}

  )
)

histogram_quantile(
  0.950000,
  sum by (fqdn, le) (
    rate(grpc_server_handling_seconds_bucket{environment="$environment", stage="$stage"}[$__interval]) and on(grpc_service,grpc_method) grpc_client_handled_total{job="gitaly"}

  )
)

histogram_quantile(
  0.950000,
  sum by (le) (
    rate(grpc_server_handling_seconds_bucket{job="gitaly", grpc_type="unary", grpc_method!~"CalculateChecksum|CommitLanguages|CreateFork|CreateRepositoryFromURL|FetchInternalRemote|FetchRemote|FindRemoteRepository|FindRemoteRootRef|Fsck|GarbageCollect|PostReceiveHook|PreReceiveHook|RepackFull|RepackIncremental|ReplicateRepository|UpdateHook|UserCherryPick|UserFFBranch|UserRebase|UserRevert|UserSquash|UserUpdateBranch", environment="$environment", stage="$stage"}[$__interval])
  )
)

sum by () (
  rate(gitaly_service_client_requests_total{job="gitaly", environment="$environment", stage="$stage"}[$__interval])
)

histogram_quantile(
  0.950000,
  sum by (fqdn, le) (
    rate(grpc_server_handling_seconds_bucket{job="gitaly", grpc_type="unary", grpc_method!~"CalculateChecksum|CommitLanguages|CreateFork|CreateRepositoryFromURL|FetchInternalRemote|FetchRemote|FindRemoteRepository|FindRemoteRootRef|Fsck|GarbageCollect|PostReceiveHook|PreReceiveHook|RepackFull|RepackIncremental|ReplicateRepository|UpdateHook|UserCherryPick|UserFFBranch|UserRebase|UserRevert|UserSquash|UserUpdateBranch", environment="$environment", stage="$stage"}[$__interval])
  )
)

sum by (fqdn) (
  rate(gitaly_service_client_requests_total{job="gitaly", environment="$environment", stage="$stage"}[$__interval])
)

max(
  rate(node_disk_reads_completed_total{environment="$environment", stage="$stage", type="gitaly"}[$__interval])
) by (fqdn)

max(
  rate(node_disk_writes_completed_total{environment="$environment", stage="$stage", type="gitaly"}[$__interval])
) by (fqdn)

max(
  rate(node_disk_read_bytes_total{environment="$environment", stage="$stage", type="gitaly"}[$__interval])
) by (fqdn)

max(
  rate(node_disk_written_bytes_total{environment="$environment", stage="$stage", type="gitaly"}[$__interval])
) by (fqdn)

sum(
  rate(node_disk_read_time_seconds_total{environment="$environment", stage="$stage", type="gitaly"}[$__interval])
) by (fqdn)

sum(
  rate(node_disk_write_time_seconds_total{environment="$environment", stage="$stage", type="gitaly"}[$__interval])
) by (fqdn)

clamp_min(
  clamp_max(
    max(
  (
    1 -
    instance:node_filesystem_avail:ratio{environment="$environment", stage="$stage", type="gitaly" ,fstype=~"ext.|xfs"}
  )
) by (device, fqdn)

  ,1)
,0)

clamp_min(
  clamp_max(
    max(
  label_replace(
    process_open_fds{environment="$environment", stage="$stage", type="gitaly"}
    /
    process_max_fds{environment="$environment", stage="$stage", type="gitaly"}
    , "client", "general", "", ""
  )
  or
  label_replace(
    ruby_file_descriptors{environment="$environment", stage="$stage", type="gitaly"}
    /
    ruby_process_max_fds{environment="$environment", stage="$stage", type="gitaly"}
    , "client", "ruby", "", ""
  )
) by (job, instance)

  ,1)
,0)

clamp_min(
  clamp_max(
    sum by (fqdn, type, tier, stage, environment) (
  go_memstats_alloc_bytes{environment="$environment", stage="$stage", type="gitaly"}
)
/
sum by (fqdn, type, tier, stage, environment) (
  node_memory_MemTotal_bytes{environment="$environment", stage="$stage", type="gitaly"}
)

  ,1)
,0)

clamp_min(clamp_max(avg_over_time(
  max_over_time(
    (rate(node_disk_read_bytes_total{environment="$environment", type="gitaly", stage="$stage", device="sdb"}[30s]) / (1258291200))[5m:30s]
  )[$__interval:1m]
)
,1),0)

clamp_min(clamp_max(avg_over_time(
  max_over_time(
    (rate(node_disk_written_bytes_total{environment="$environment", type="gitaly", stage="$stage", device="sdb"}[30s]) / (419430400))[5m:30s]
 )[$__interval:1m]
)
,1),0)

sum(
  rate(
    gitaly_rate_limiting_acquiring_seconds_bucket{
      environment="$environment", type="gitaly", stage="$stage",
      le="60"
    }[$__interval]
  )
) by (environment, tier, type, stage, fqdn, grpc_method)
/
sum(
  rate(
    gitaly_rate_limiting_acquiring_seconds_bucket{
      environment="$environment", type="gitaly", stage="$stage",
      le="+Inf"
    }[$__interval]
  )
) by (environment, tier, type, stage, fqdn, grpc_method)

max by (type, stage, component) (
  clamp_max(
    gitlab_component_saturation:ratio{environment="$environment", type="gitaly", stage=~"|$stage"}
    ,
    1
  ) >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:avg_over_time_1w{
        environment="$environment",
        type="gitaly", stage=~"|$stage"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="gitaly", stage=~"|$stage"
        }
      , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:predict_linear_2w{
        environment="$environment",
        type="gitaly", stage=~"|$stage"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="gitaly", stage=~"|$stage"
        }
    , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

clamp_min(clamp_max(
  max(
    gitlab_component_saturation:ratio{
      type="gitaly",
      environment="$environment",
      stage=~"|$stage"
    }
  ) by (component)
  ,1)
,0)
