targets.expr:
min(
  min_over_time(
    gitlab_service_apdex:ratio{environment="$environment", type="gitaly", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  max_over_time(
    gitlab_service_errors:ratio{environment="$environment", type="gitaly", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  avg_over_time(
    gitlab_service_ops:rate{environment="$environment", type="gitaly", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

sum(
  rate(
    gitaly_rate_limiting_acquiring_seconds_bucket{
      environment="$environment",
      stage="$stage",
      le="60"
    }[$__interval]
  )
) by (environment, tier, type, stage, fqdn, grpc_method)
/
sum(
  rate(
    gitaly_rate_limiting_acquiring_seconds_bucket{
      environment="$environment",
      stage="$stage",
      le="+Inf"
    }[$__interval]
  )
) by (environment, tier, type, stage, fqdn, grpc_method)

min(
  min_over_time(
    gitlab_service_apdex:ratio{environment="$environment", type="gitaly", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  max_over_time(
    gitlab_service_errors:ratio{environment="$environment", type="gitaly", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  avg_over_time(
    gitlab_service_ops:rate{environment="$environment", type="gitaly", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

clamp_min(
  clamp_max(
    max(
  (
    1 -
    instance:node_filesystem_avail:ratio{environment="$environment", type="gitaly", stage="$stage" ,fstype=~"ext.|xfs"}
  )
) by (device, fqdn)

  ,1)
,0)

clamp_min(
  clamp_max(
    max(
  label_replace(
    process_open_fds{environment="$environment", type="gitaly", stage="$stage"}
    /
    process_max_fds{environment="$environment", type="gitaly", stage="$stage"}
    , "client", "general", "", ""
  )
  or
  label_replace(
    ruby_file_descriptors{environment="$environment", type="gitaly", stage="$stage"}
    /
    ruby_process_max_fds{environment="$environment", type="gitaly", stage="$stage"}
    , "client", "ruby", "", ""
  )
) by (job, instance)

  ,1)
,0)

clamp_min(
  clamp_max(
    sum by (fqdn, type, tier, stage, environment) (
  go_memstats_alloc_bytes{environment="$environment", type="gitaly", stage="$stage"}
)
/
sum by (fqdn, type, tier, stage, environment) (
  node_memory_MemTotal_bytes{environment="$environment", type="gitaly", stage="$stage"}
)

  ,1)
,0)

histogram_quantile(
  0.995000,
  sum by (le) (
    rate(grpc_server_handling_seconds_bucket{job="gitaly", grpc_type="unary", grpc_method!~"GarbageCollect|Fsck|RepackFull|RepackIncremental|CommitLanguages|CreateRepositoryFromURL|UserRebase|UserSquash|CreateFork|UserUpdateBranch|FindRemoteRepository|UserCherryPick|FetchRemote|UserRevert|FindRemoteRootRef", environment="$environment", type="gitaly", stage="$stage"}[$__interval])
  )
)

histogram_quantile(
  0.995000,
  sum by (fqdn, le) (
    rate(grpc_server_handling_seconds_bucket{job="gitaly", grpc_type="unary", grpc_method!~"GarbageCollect|Fsck|RepackFull|RepackIncremental|CommitLanguages|CreateRepositoryFromURL|UserRebase|UserSquash|CreateFork|UserUpdateBranch|FindRemoteRepository|UserCherryPick|FetchRemote|UserRevert|FindRemoteRootRef", environment="$environment", type="gitaly", stage="$stage"}[$__interval])
  )
)

histogram_quantile(
  0.995000,
  sum by (le) (
    rate(grpc_server_handling_seconds_bucket{job="gitaly",grpc_type="unary", environment="$environment", type="gitaly", stage="$stage"}[$__interval]) and on(grpc_service,grpc_method) grpc_client_handled_total{job="gitaly"}

  )
)

histogram_quantile(
  0.995000,
  sum by (fqdn, le) (
    rate(grpc_server_handling_seconds_bucket{job="gitaly",grpc_type="unary", environment="$environment", type="gitaly", stage="$stage"}[$__interval]) and on(grpc_service,grpc_method) grpc_client_handled_total{job="gitaly"}

  )
)

max by (type, stage, component) (
  clamp_max(
    gitlab_component_saturation:ratio{environment="$environment", type="gitaly", stage=~"|$stage"}
    ,
    1
  ) >= on(component, monitor, env) group_left slo:max:soft:gitlab_component_saturation:ratio
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:avg_over_time_1w{
        environment="$environment",
        type="gitaly", stage=~"|$stage"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="gitaly", stage=~"|$stage"
        }
      , 1
    )
    >= on(component, monitor, env) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:predict_linear_2w{
        environment="$environment",
        type="gitaly", stage=~"|$stage"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="gitaly", stage=~"|$stage"
        }
    , 1
    )
    >= on(component, monitor, env) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

clamp_min(clamp_max(
  max(
    gitlab_component_saturation:ratio{
      type="gitaly",
      environment="$environment",
      stage=~"|$stage"
    }
  ) by (component)
  ,1)
,0)
