clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, cluster, pod) (
  clamp_min(
    clamp_max(
      (
        go_memstats_alloc_bytes{env="$environment",environment="$environment",stage="$stage",type="kas"}
        / on(environment, tier, type, stage, cluster, pod) group_left()
        topk by(environment, tier, type, stage, cluster, pod) (1,
          container_spec_memory_limit_bytes:labeled{container=~"gitlab-workhorse|kas|registry|thanos-store",env="$environment",environment="$environment",stage="$stage",type="kas"}
        )
      )
      or
      (
        go_memstats_alloc_bytes{type="monitoring", env="$environment",environment="$environment",stage="$stage",type="kas"}
        / on(environment, tier, type, cluster, pod) group_left()
        topk by (environment, tier, type, cluster, pod) (1,
          container_spec_memory_limit_bytes:labeled{type="monitoring", container=~"gitlab-workhorse|kas|registry|thanos-store"}
        )
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)

