  clamp_max(
    max by(environment, tier, type, stage, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{env="$environment",environment="$environment",type="mailroom"}
        /
        process_max_fds{env="$environment",environment="$environment",type="mailroom"}
      )
      or
      (
        ruby_file_descriptors{env="$environment",environment="$environment",type="mailroom"}
        /
        ruby_process_max_fds{env="$environment",environment="$environment",type="mailroom"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)
