targets.expr:
clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{env="$environment",environment="$environment",stage="$stage",type="mailroom"}
        /
        process_max_fds{env="$environment",environment="$environment",stage="$stage",type="mailroom"}
      )
      or
      (
        ruby_file_descriptors{env="$environment",environment="$environment",stage="$stage",type="mailroom"}
        /
        ruby_process_max_fds{env="$environment",environment="$environment",stage="$stage",type="mailroom"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)

clamp_min(clamp_max(
  max(
    gitlab_component_saturation:ratio{
      type="mailroom",
      environment="$environment",
      stage=~"|$stage"
    }
  ) by (component)
  ,1)
,0)
