clamp_min(
  avg by (type) (
    gitlab_service_ops:rate:prediction{env="ops",environment="ops",monitor="global",stage="$stage",type="monitoring"}
    + (2) *
    gitlab_service_ops:rate:stddev_over_time_1w{env="ops",environment="ops",monitor="global",stage="$stage",type="monitoring"}
  )
  or
  (
      sum by (type) (gitlab_service_ops:rate:prediction{env="ops",environment="ops",monitor!~"global|",stage="$stage",type="monitoring"})
      + (2) *
      sum by (type) (gitlab_service_ops:rate:stddev_over_time_1w{env="ops",environment="ops",monitor!~"global|",stage="$stage",type="monitoring"})
  ),
  0
)

