targets.expr:
sum by () (
  sum by (environment) (
    avg_over_time(
      stackdriver_nat_gateway_router_googleapis_com_nat_dropped_received_packets_count{metric_prefix="router.googleapis.com/nat",  ip_protocol="6", project_id=~"gitlab-staging-1|gitlab-production"}[$__interval]
    )
  )
)

sum by () (
  sum by (environment) (
    avg_over_time(
      stackdriver_nat_gateway_router_googleapis_com_nat_received_packets_count{metric_prefix="router.googleapis.com/nat",  ip_protocol="6", project_id=~"gitlab-staging-1|gitlab-production"}[$__interval]
    )
  )
)

sum by () (
  sum by (environment) (
    avg_over_time(
      stackdriver_nat_gateway_router_googleapis_com_nat_dropped_sent_packets_count{metric_prefix="router.googleapis.com/nat",  ip_protocol="6", project_id=~"gitlab-staging-1|gitlab-production"}[$__interval]
    )
  )
)

sum by () (
  sum by (environment) (
    avg_over_time(
      stackdriver_nat_gateway_router_googleapis_com_nat_sent_packets_count{metric_prefix="router.googleapis.com/nat",  ip_protocol="6", project_id=~"gitlab-staging-1|gitlab-production"}[$__interval]
    )
  )
)

clamp_min(clamp_max(
  max(
    gitlab_component_saturation:ratio{
      type="nat",
      environment="$environment",
      stage=~"|$stage"
    }
  ) by (component)
  ,1)
,0)
