targets.expr:
sum by (component,violation_type) (
  label_replace(
    sum by (component)(
      sum_over_time(
        gitlab:component:stage_group:execution:apdex:weight:score_1h{environment="gprd",monitor="global",stage="main",stage_group="access"}[28d]
      ) -
      # Request with satisfactory apdex
      sum_over_time(
        gitlab:component:stage_group:execution:apdex:success:rate_1h{environment="gprd",monitor="global",stage="main",stage_group="access"}[28d]
      )
    ),
    'violation_type', 'apdex' , '', ''
  )
  or
  label_replace(
    sum by (component)(
      sum_over_time(
        gitlab:component:stage_group:execution:error:rate_1h{environment="gprd",monitor="global",stage="main",stage_group="access"}[28d]
      )
    ),
    'violation_type', 'error' , '', ''
  )
)
