targets.expr:
clamp_min(
  avg by (type) (
    gitlab_service_ops:rate:prediction{env="$environment", environment="$environment", monitor="global", stage="$stage", type="nfs"}
    + (2) *
    gitlab_service_ops:rate:stddev_over_time_1w{env="$environment", environment="$environment", monitor="global", stage="$stage", type="nfs"}
  )
  or
  (
      sum by (type) (gitlab_service_ops:rate:prediction{environment="$environment", monitor!~"global|", stage="$stage", type="nfs"})
      + (2) *
      sum by (type) (gitlab_service_ops:rate:stddev_over_time_1w{environment="$environment", monitor!~"global|", stage="$stage", type="nfs"})
  ),
  0
)

clamp_min(
  avg by (type) (
    gitlab_service_ops:rate:prediction{env="$environment", environment="$environment", monitor="global", stage="$stage", type="nfs"}
    + (-2) *
    gitlab_service_ops:rate:stddev_over_time_1w{env="$environment", environment="$environment", monitor="global", stage="$stage", type="nfs"}
  )
  or
  (
      sum by (type) (gitlab_service_ops:rate:prediction{environment="$environment", monitor!~"global|", stage="$stage", type="nfs"})
      + (-2) *
      sum by (type) (gitlab_service_ops:rate:stddev_over_time_1w{environment="$environment", monitor!~"global|", stage="$stage", type="nfs"})
  ),
  0
)

sum(
  gitlab_component_errors:rate_5m{component="nfs_service", environment="$environment", monitor!~"global|", stage="$stage", type="nfs"}
) by (component)
/
sum(
  gitlab_component_ops:rate_5m{component="nfs_service", environment="$environment", monitor!~"global|", stage="$stage", type="nfs"}
) by (component)

sum(
  avg_over_time(
    gitlab_component_ops:rate_5m{component="nfs_service", environment="$environment", monitor!~"global|", stage="$stage", type="nfs"}[$__interval]
  )
) by (component)

clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{environment="$environment", stage="$stage", type="nfs"}
        /
        process_max_fds{environment="$environment", stage="$stage", type="nfs"}
      )
      or
      (
        ruby_file_descriptors{environment="$environment", stage="$stage", type="nfs"}
        /
        ruby_process_max_fds{environment="$environment", stage="$stage", type="nfs"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)

max by (type, stage, component) (
  clamp_max(
    gitlab_component_saturation:ratio{environment="$environment", type="nfs", stage=~"|$stage"}
    ,
    1
  ) >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:avg_over_time_1w{
        environment="$environment",
        type="nfs", stage=~"|$stage"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="nfs", stage=~"|$stage"
        }
      , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:predict_linear_2w{
        environment="$environment",
        type="nfs", stage=~"|$stage"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="nfs", stage=~"|$stage"
        }
    , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

clamp_min(clamp_max(
  max(
    gitlab_component_saturation:ratio{
      type="nfs",
      environment="$environment",
      stage=~"|$stage"
    }
  ) by (component)
  ,1)
,0)
