targets.expr:
clamp_min(
  avg by (type) (
    gitlab_service_ops:rate:prediction{env="$environment",environment="$environment",monitor="global",stage="$stage",type="pages"}
    + (2) *
    gitlab_service_ops:rate:stddev_over_time_1w{env="$environment",environment="$environment",monitor="global",stage="$stage",type="pages"}
  )
  or
  (
      sum by (type) (gitlab_service_ops:rate:prediction{env="$environment",environment="$environment",monitor!~"global|",stage="$stage",type="pages"})
      + (2) *
      sum by (type) (gitlab_service_ops:rate:stddev_over_time_1w{env="$environment",environment="$environment",monitor!~"global|",stage="$stage",type="pages"})
  ),
  0
)

clamp_min(
  avg by (type) (
    gitlab_service_ops:rate:prediction{env="$environment",environment="$environment",monitor="global",stage="$stage",type="pages"}
    + (-2) *
    gitlab_service_ops:rate:stddev_over_time_1w{env="$environment",environment="$environment",monitor="global",stage="$stage",type="pages"}
  )
  or
  (
      sum by (type) (gitlab_service_ops:rate:prediction{env="$environment",environment="$environment",monitor!~"global|",stage="$stage",type="pages"})
      + (-2) *
      sum by (type) (gitlab_service_ops:rate:stddev_over_time_1w{env="$environment",environment="$environment",monitor!~"global|",stage="$stage",type="pages"})
  ),
  0
)

clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{env="$environment",environment="$environment",stage="$stage",type="pages"}
        /
        process_max_fds{env="$environment",environment="$environment",stage="$stage",type="pages"}
      )
      or
      (
        ruby_file_descriptors{env="$environment",environment="$environment",stage="$stage",type="pages"}
        /
        ruby_process_max_fds{env="$environment",environment="$environment",stage="$stage",type="pages"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)

clamp_min(clamp_max(
  max(
    gitlab_component_saturation:ratio{
      type="pages",
      environment="$environment",
      stage=~"|$stage"
    }
  ) by (component)
  ,1)
,0)
