targets.expr:
clamp_min(
  avg by (type) (
    gitlab_service_ops:rate:prediction{env="$environment", environment="$environment", monitor="global", stage="main", type="patroni"}
    + (2) *
    gitlab_service_ops:rate:stddev_over_time_1w{env="$environment", environment="$environment", monitor="global", stage="main", type="patroni"}
  )
  or
  (
      sum by (type) (gitlab_service_ops:rate:prediction{environment="$environment", monitor!~"global|", stage="main", type="patroni"})
      + (2) *
      sum by (type) (gitlab_service_ops:rate:stddev_over_time_1w{environment="$environment", monitor!~"global|", stage="main", type="patroni"})
  ),
  0
)

clamp_min(
  avg by (type) (
    gitlab_service_ops:rate:prediction{env="$environment", environment="$environment", monitor="global", stage="main", type="patroni"}
    + (-2) *
    gitlab_service_ops:rate:stddev_over_time_1w{env="$environment", environment="$environment", monitor="global", stage="main", type="patroni"}
  )
  or
  (
      sum by (type) (gitlab_service_ops:rate:prediction{environment="$environment", monitor!~"global|", stage="main", type="patroni"})
      + (-2) *
      sum by (type) (gitlab_service_ops:rate:stddev_over_time_1w{environment="$environment", monitor!~"global|", stage="main", type="patroni"})
  ),
  0
)

sum(
  avg_over_time(
    gitlab_component_ops:rate{component="pgbouncer", environment="$environment", monitor!~"global|", stage="main", type="patroni"}[$__interval]
  )
) by (component)

sum(
  gitlab_component_errors:rate{component="service", environment="$environment", monitor!~"global|", stage="main", type="patroni"}
) by (component)
/
sum(
  gitlab_component_ops:rate{component="service", environment="$environment", monitor!~"global|", stage="main", type="patroni"}
) by (component)

sum(
  avg_over_time(
    gitlab_component_ops:rate{component="service", environment="$environment", monitor!~"global|", stage="main", type="patroni"}[$__interval]
  )
) by (component)

clamp_min(
  clamp_max(
    sum without (state, datname) (
  pg_stat_activity_count{datname="gitlabhq_production", state!="idle", environment="$environment", stage="main", type="patroni"}
)
/ pg_settings_max_connections{environment="$environment", stage="main", type="patroni"}

  ,1)
,0)

clamp_min(
  clamp_max(
    max(
  (
    1 -
    instance:node_filesystem_avail:ratio{environment="$environment", stage="main", type="patroni" ,fstype=~"ext.|xfs"}
  )
) by (device, fqdn)

  ,1)
,0)

clamp_min(
  clamp_max(
    max(
  label_replace(
    process_open_fds{environment="$environment", stage="main", type="patroni"}
    /
    process_max_fds{environment="$environment", stage="main", type="patroni"}
    , "client", "general", "", ""
  )
  or
  label_replace(
    ruby_file_descriptors{environment="$environment", stage="main", type="patroni"}
    /
    ruby_process_max_fds{environment="$environment", stage="main", type="patroni"}
    , "client", "ruby", "", ""
  )
) by (job, instance)

  ,1)
,0)

clamp_min(clamp_max(sum(
  rate(
    namedprocess_namegroup_cpu_seconds_total{groupname=~"pgbouncer.*", type="patroni", environment="$environment"}[1m]
  )
) by (groupname, fqdn, type, tier, stage, environment)
,1),0)

sum(
  rate(
    namedprocess_namegroup_cpu_seconds_total{
      environment="$environment",
      groupname="patroni",
      type="patroni",
      stage="main"
    }[$__interval]
  )
) without (mode)

max(
    namedprocess_namegroup_open_filedesc{
      environment="$environment",
      groupname="patroni",
      type="patroni",
      stage="main"
    }
) by (fqdn)

sum(
    namedprocess_namegroup_num_threads{
      environment="$environment",
      groupname="patroni",
      type="patroni",
      stage="main"
    }
) by (fqdn)

sum(
    namedprocess_namegroup_memory_bytes{
      environment="$environment",
      groupname="patroni",
      type="patroni",
      stage="main"
    }
) by (fqdn)

max by (type, stage, component) (
  clamp_max(
    gitlab_component_saturation:ratio{environment="$environment", type="patroni", stage=~"|main"}
    ,
    1
  ) >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:avg_over_time_1w{
        environment="$environment",
        type="patroni", stage=~"|main"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="patroni", stage=~"|main"
        }
      , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:predict_linear_2w{
        environment="$environment",
        type="patroni", stage=~"|main"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="patroni", stage=~"|main"
        }
    , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

clamp_min(clamp_max(
  max(
    gitlab_component_saturation:ratio{
      type="patroni",
      environment="$environment",
      stage=~"|main"
    }
  ) by (component)
  ,1)
,0)
