targets.expr:
min(
  min_over_time(
    gitlab_service_apdex:ratio{environment="$environment", type="patroni", stage="main"}[$__interval] offset 1w
  )
) by (type)

max(
  max_over_time(
    gitlab_service_errors:ratio{environment="$environment", type="patroni", stage="main"}[$__interval] offset 1w
  )
) by (type)

max(
  avg_over_time(
    gitlab_service_ops:rate{environment="$environment", type="patroni", stage="main"}[$__interval] offset 1w
  )
) by (type)

sum(
  rate(
    namedprocess_namegroup_cpu_seconds_total{
      environment="$environment",
      groupname="patroni",
      type="patroni",
      stage="main"
    }[$__interval]
  )
) without (mode)

max(
    namedprocess_namegroup_open_filedesc{
      environment="$environment",
      groupname="patroni",
      type="patroni",
      stage="main"
    }
) by (fqdn)

sum(
    namedprocess_namegroup_num_threads{
      environment="$environment",
      groupname="patroni",
      type="patroni",
      stage="main"
    }
) by (fqdn)

sum(
    namedprocess_namegroup_memory_bytes{
      environment="$environment",
      groupname="patroni",
      type="patroni",
      stage="main"
    }
) by (fqdn)

min(
  min_over_time(
    gitlab_service_apdex:ratio{environment="$environment", type="patroni", stage="main"}[$__interval] offset 1w
  )
) by (type)

max(
  max_over_time(
    gitlab_service_errors:ratio{environment="$environment", type="patroni", stage="main"}[$__interval] offset 1w
  )
) by (type)

max(
  avg_over_time(
    gitlab_service_ops:rate{environment="$environment", type="patroni", stage="main"}[$__interval] offset 1w
  )
) by (type)

sum by () (
  changes(pg_stat_database_xact_rollback{type="patroni", tier="db", environment="$environment", type="patroni", stage="main"}[$__interval])
)

sum by (fqdn) (
  changes(pg_stat_database_xact_rollback{type="patroni", tier="db", environment="$environment", type="patroni", stage="main"}[$__interval])
)

clamp_min(
  clamp_max(
    sum without (state, datname) (
  pg_stat_activity_count{datname="gitlabhq_production", state!="idle", environment="$environment", type="patroni", stage="main"}
)
/ pg_settings_max_connections{environment="$environment", type="patroni", stage="main"}

  ,1)
,0)

clamp_min(
  clamp_max(
    max(
  (
    1 -
    instance:node_filesystem_avail:ratio{environment="$environment", type="patroni", stage="main" ,fstype=~"ext.|xfs"}
  )
) by (device, fqdn)

  ,1)
,0)

clamp_min(
  clamp_max(
    max(
  label_replace(
    process_open_fds{environment="$environment", type="patroni", stage="main"}
    /
    process_max_fds{environment="$environment", type="patroni", stage="main"}
    , "client", "general", "", ""
  )
  or
  label_replace(
    ruby_file_descriptors{environment="$environment", type="patroni", stage="main"}
    /
    ruby_process_max_fds{environment="$environment", type="patroni", stage="main"}
    , "client", "ruby", "", ""
  )
) by (job, instance)

  ,1)
,0)

max by (type, stage, component) (
  clamp_max(
    gitlab_component_saturation:ratio{environment="$environment", type="patroni", stage=~"|main"}
    ,
    1
  ) >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:avg_over_time_1w{
        environment="$environment",
        type="patroni", stage=~"|main"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="patroni", stage=~"|main"
        }
      , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:predict_linear_2w{
        environment="$environment",
        type="patroni", stage=~"|main"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="patroni", stage=~"|main"
        }
    , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

clamp_min(clamp_max(
  max(
    gitlab_component_saturation:ratio{
      type="patroni",
      environment="$environment",
      stage=~"|main"
    }
  ) by (component)
  ,1)
,0)
