targets.expr:
min(
  min_over_time(
    gitlab_service_apdex:ratio{environment="$environment", type="praefect", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  max_over_time(
    gitlab_service_errors:ratio{environment="$environment", type="praefect", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  avg_over_time(
    gitlab_service_ops:rate{environment="$environment", type="praefect", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

min(
  min_over_time(
    gitlab_component_apdex:ratio{environment="$environment", type="praefect", stage="$stage", component="proxy"}[$__interval]
  )
) by (component)

max(
  gitlab_component_errors:rate{environment="$environment", type="praefect", stage="$stage", component="proxy"}
  /
  gitlab_component_ops:rate{environment="$environment", type="praefect", stage="$stage", component="proxy"}
) by (component)

1 +
max(
  avg_over_time(
    gitlab_component_ops:rate{environment="$environment", type="praefect", stage="$stage", component="proxy"}[$__interval]
  )
) by (component)

histogram_quantile(
  0.995000,
  sum by (le) (
    rate(grpc_server_handling_seconds_bucket{job="praefect", grpc_type="unary", grpc_method!~"CalculateChecksum|CommitLanguages|CreateFork|CreateRepositoryFromURL|FetchInternalRemote|FetchRemote|FindRemoteRepository|FindRemoteRootRef|Fsck|GarbageCollect|RepackFull|RepackIncremental|ReplicateRepository|UserCherryPick|UserFFBranch|UserRebase|UserRevert|UserSquash|UserUpdateBranch", environment="$environment", stage="$stage"}[$__interval])
  )
)

histogram_quantile(
  0.995000,
  sum by (fqdn, le) (
    rate(grpc_server_handling_seconds_bucket{job="praefect", grpc_type="unary", grpc_method!~"CalculateChecksum|CommitLanguages|CreateFork|CreateRepositoryFromURL|FetchInternalRemote|FetchRemote|FindRemoteRepository|FindRemoteRootRef|Fsck|GarbageCollect|RepackFull|RepackIncremental|ReplicateRepository|UserCherryPick|UserFFBranch|UserRebase|UserRevert|UserSquash|UserUpdateBranch", environment="$environment", stage="$stage"}[$__interval])
  )
)

clamp_min(
  clamp_max(
    max(
  (
    1 -
    instance:node_filesystem_avail:ratio{environment="$environment", stage="$stage", type="praefect" ,fstype=~"ext.|xfs"}
  )
) by (device, fqdn)

  ,1)
,0)

clamp_min(
  clamp_max(
    max(
  label_replace(
    process_open_fds{environment="$environment", stage="$stage", type="praefect"}
    /
    process_max_fds{environment="$environment", stage="$stage", type="praefect"}
    , "client", "general", "", ""
  )
  or
  label_replace(
    ruby_file_descriptors{environment="$environment", stage="$stage", type="praefect"}
    /
    ruby_process_max_fds{environment="$environment", stage="$stage", type="praefect"}
    , "client", "ruby", "", ""
  )
) by (job, instance)

  ,1)
,0)

clamp_min(
  clamp_max(
    sum by (fqdn, type, tier, stage, environment) (
  go_memstats_alloc_bytes{environment="$environment", stage="$stage", type="praefect"}
)
/
sum by (fqdn, type, tier, stage, environment) (
  node_memory_MemTotal_bytes{environment="$environment", stage="$stage", type="praefect"}
)

  ,1)
,0)

max by (type, stage, component) (
  clamp_max(
    gitlab_component_saturation:ratio{environment="$environment", type="praefect", stage=~"|$stage"}
    ,
    1
  ) >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:avg_over_time_1w{
        environment="$environment",
        type="praefect", stage=~"|$stage"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="praefect", stage=~"|$stage"
        }
      , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:predict_linear_2w{
        environment="$environment",
        type="praefect", stage=~"|$stage"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="praefect", stage=~"|$stage"
        }
    , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

clamp_min(clamp_max(
  max(
    gitlab_component_saturation:ratio{
      type="praefect",
      environment="$environment",
      stage=~"|$stage"
    }
  ) by (component)
  ,1)
,0)
