targets.expr:
min(
  min_over_time(
    gitlab_service_apdex:ratio{environment="$environment", type="redis-cache", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  max_over_time(
    gitlab_service_errors:ratio{environment="$environment", type="redis-cache", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  avg_over_time(
    gitlab_service_ops:rate{environment="$environment", type="redis-cache", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

min(
  min_over_time(
    gitlab_service_apdex:ratio{environment="$environment", type="redis-cache", stage="main"}[$__interval] offset 1w
  )
) by (type)

max(
  max_over_time(
    gitlab_service_errors:ratio{environment="$environment", type="redis-cache", stage="main"}[$__interval] offset 1w
  )
) by (type)

max(
  avg_over_time(
    gitlab_service_ops:rate{environment="$environment", type="redis-cache", stage="main"}[$__interval] offset 1w
  )
) by (type)

clamp_min(
  clamp_max(
    max(
  (
    1 -
    instance:node_filesystem_avail:ratio{type="redis-cache", environment="$environment" ,fstype=~"ext.|xfs"}
  )
) by (device, fqdn)

  ,1)
,0)

clamp_min(
  clamp_max(
    max(
  label_replace(
    process_open_fds{type="redis-cache", environment="$environment"}
    /
    process_max_fds{type="redis-cache", environment="$environment"}
    , "client", "general", "", ""
  )
  or
  label_replace(
    ruby_file_descriptors{type="redis-cache", environment="$environment"}
    /
    ruby_process_max_fds{type="redis-cache", environment="$environment"}
    , "client", "ruby", "", ""
  )
) by (job, instance)

  ,1)
,0)

max by (type, stage, component) (
  clamp_max(
    gitlab_component_saturation:ratio{environment="$environment", type="redis-cache", stage=~"|main"}
    ,
    1
  ) >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:avg_over_time_1w{
        environment="$environment",
        type="redis-cache", stage=~"|main"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="redis-cache", stage=~"|main"
        }
      , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:predict_linear_2w{
        environment="$environment",
        type="redis-cache", stage=~"|main"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="redis-cache", stage=~"|main"
        }
    , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

clamp_min(clamp_max(
  max(
    gitlab_component_saturation:ratio{
      type="redis-cache",
      environment="$environment",
      stage=~"|main"
    }
  ) by (component)
  ,1)
,0)
