targets.expr:
max(
  avg_over_time(
    gitlab_service_ops:rate{environment="$environment", type="redis", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

1 +
max(
  avg_over_time(
    gitlab_component_ops:rate{environment="$environment", type="redis", stage="$stage", component="primary_server"}[$__interval]
  )
) by (component)

sum by () (
  rate(redis_commands_processed_total{type="redis", environment="$environment", stage="$stage"}[$__interval]) and on (instance) redis_instance_info{role="master"}
)

sum by (fqdn) (
  rate(redis_commands_processed_total{type="redis", environment="$environment", stage="$stage"}[$__interval]) and on (instance) redis_instance_info{role="master"}
)

sum by () (
  rate(redis_commands_processed_total{type="redis", environment="$environment", stage="$stage"}[$__interval]) and on (instance) redis_instance_info{role="slave"}
)

sum by (fqdn) (
  rate(redis_commands_processed_total{type="redis", environment="$environment", stage="$stage"}[$__interval]) and on (instance) redis_instance_info{role="slave"}
)

clamp_min(
  clamp_max(
    max(
  (
    1 -
    instance:node_filesystem_avail:ratio{environment="$environment", stage="$stage", type="redis" ,fstype=~"ext.|xfs"}
  )
) by (device, fqdn)

  ,1)
,0)

clamp_min(
  clamp_max(
    max(
  label_replace(
    process_open_fds{environment="$environment", stage="$stage", type="redis"}
    /
    process_max_fds{environment="$environment", stage="$stage", type="redis"}
    , "client", "general", "", ""
  )
  or
  label_replace(
    ruby_file_descriptors{environment="$environment", stage="$stage", type="redis"}
    /
    ruby_process_max_fds{environment="$environment", stage="$stage", type="redis"}
    , "client", "ruby", "", ""
  )
) by (job, instance)

  ,1)
,0)

max by (type, stage, component) (
  clamp_max(
    gitlab_component_saturation:ratio{environment="$environment", type="redis", stage=~"|$stage"}
    ,
    1
  ) >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:avg_over_time_1w{
        environment="$environment",
        type="redis", stage=~"|$stage"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="redis", stage=~"|$stage"
        }
      , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:predict_linear_2w{
        environment="$environment",
        type="redis", stage=~"|$stage"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="redis", stage=~"|$stage"
        }
    , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

clamp_min(clamp_max(
  max(
    gitlab_component_saturation:ratio{
      type="redis",
      environment="$environment",
      stage=~"|$stage"
    }
  ) by (component)
  ,1)
,0)
