targets.expr:
clamp_min(clamp_max((
  sum by (type) (
  rate(gitlab_redis_client_requests_duration_seconds_bucket{env="$environment",environment="$environment",le="+Inf",storage="shared_state"}[$__interval])
)

  -
  (
    (
  sum by (type) (
    rate(gitlab_redis_client_requests_duration_seconds_bucket{env="$environment",environment="$environment",le="0.5",storage="shared_state"}[$__interval])
  )
  +
  sum by (type) (
    rate(gitlab_redis_client_requests_duration_seconds_bucket{env="$environment",environment="$environment",le="0.75",storage="shared_state"}[$__interval])
  )
)
/
2

  )
)
/ ignoring(type) group_left()
(
  sum by () (
  rate(gitlab_redis_client_requests_duration_seconds_bucket{env="$environment",environment="$environment",le="+Inf",storage="shared_state"}[$__interval])
)

)

,1),0)

clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{env="$environment",environment="$environment",stage="$stage",type="redis"}
        /
        process_max_fds{env="$environment",environment="$environment",stage="$stage",type="redis"}
      )
      or
      (
        ruby_file_descriptors{env="$environment",environment="$environment",stage="$stage",type="redis"}
        /
        ruby_process_max_fds{env="$environment",environment="$environment",stage="$stage",type="redis"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)

clamp_min(clamp_max(max(
  max_over_time(instance:redis_cpu_usage:rate1m{environment="$environment", type="redis", fqdn=~"redis-\\d\\d.*"}[$__interval])
    and on (instance) redis_instance_info{role="master"}
) by (fqdn)
,1),0)

clamp_min(clamp_max(max(
  max_over_time(instance:redis_cpu_usage:rate1m{environment="$environment", type="redis", fqdn=~"redis-\\d\\d.*"}[$__interval])
    and on (instance) redis_instance_info{role="slave"}
) by (fqdn)
,1),0)

sum(rate(redis_commands_duration_seconds_total{environment="$environment", type="redis"}[$__interval])
  and on (instance) redis_instance_info{role="master"}
) by (cmd)
/
sum(rate(redis_commands_total{environment="$environment", type="redis"}[$__interval])) by (cmd)

sum(rate(redis_commands_duration_seconds_total{environment="$environment", type="redis"}[$__interval])
  and on (instance) redis_instance_info{role="slave"}
) by (cmd)
/
sum(rate(redis_commands_total{environment="$environment", type="redis"}[$__interval])) by (cmd)

clamp_min(clamp_max(
  max(
    gitlab_component_saturation:ratio{
      type="redis",
      environment="$environment",
      stage=~"|$stage"
    }
  ) by (component)
  ,1)
,0)
