targets.expr:
clamp_min(clamp_max(
        max(
          1 -
          (
            (
              node_memory_MemFree_bytes{type="sidekiq", environment="$environment", stage="$stage"} +
              node_memory_Buffers_bytes{type="sidekiq", environment="$environment", stage="$stage"} +
              node_memory_Cached_bytes{type="sidekiq", environment="$environment", stage="$stage"}
            )
          )
          /
          node_memory_MemTotal_bytes{type="sidekiq", environment="$environment", stage="$stage"}
        ) by (priority)
      ,1),0)

histogram_quantile(0.90,
  sum(job_environment:gitlab_transaction_duration_seconds_bucket:rate5m{
    environment="$environment",
    type="sidekiq",
    stage="$stage"
  }) by (le, job)
)

sum(
  rate(
    gitlab_transaction_rails_queue_duration_total{
      environment="$environment",
      type="sidekiq",
      stage="$stage"}
      [$__interval]
  )
) by (job)

sum(
  rate(
    gitlab_cache_operations_total{
      environment="$environment",
      type="sidekiq",
      stage="$stage"}[$__interval]
  )
) by (job)

sum(
  changes(
    ruby_process_start_time_seconds{
      environment="$environment",
      type="sidekiq",
      stage="$stage",
      job="gitlab-rails"
    }[$__interval]
  )
)

avg(
  avg_over_time(
    db_load_balancing_hosts{
      environment="$environment",
      type="sidekiq",
      stage="$stage"}[$__interval]
  )
) by (job)

min(
  min_over_time(
    gitlab_service_apdex:ratio{environment="$environment", type="sidekiq", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  max_over_time(
    gitlab_service_errors:ratio{environment="$environment", type="sidekiq", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  avg_over_time(
    gitlab_service_ops:rate{environment="$environment", type="sidekiq", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

clamp_min(clamp_max(
        1 -
        (
          (
            node_memory_MemFree_bytes{type="sidekiq", environment="$environment", stage="$stage"} +
            node_memory_Buffers_bytes{type="sidekiq", environment="$environment", stage="$stage"} +
            node_memory_Cached_bytes{type="sidekiq", environment="$environment", stage="$stage"}
          )
        )
        /
        node_memory_MemTotal_bytes{type="sidekiq", environment="$environment", stage="$stage"}
      ,1),0)

sort(
          clamp_min(
            clamp_max(
              max(
                gitlab_component_saturation:ratio:avg_over_time_1w{environment="$environment", type="sidekiq", stage=~"|$stage"}
                + 2 * gitlab_component_saturation:ratio:stddev_over_time_1w{environment="$environment", type="sidekiq", stage=~"|$stage"}
              ) by (component),
            1),
          0)
        )

sort(
          clamp_min(
            clamp_max(
              max(
                gitlab_component_saturation:ratio:predict_linear_2w{environment="$environment", type="sidekiq", stage=~"|$stage"}
                + 2 * gitlab_component_saturation:ratio:stddev_over_time_1w{environment="$environment", type="sidekiq", stage=~"|$stage"}
              ) by (component),
            1),
          0)
        )

clamp_min(clamp_max(
          max(
            gitlab_component_saturation:ratio{
              type="sidekiq",
              environment="$environment",
              stage=~"|$stage"
            }
          ) by (component)
          ,1),0)

clamp_min(
          clamp_max(
            max(
              gitlab_component_saturation:ratio:avg_over_time_1w{
                type="sidekiq",
                environment="$environment",
                stage=~"$stage|"
              }
            ) by (component)
          ,1),0)
