targets.expr:
histogram_quantile(0.90,
  sum(job_environment:gitlab_transaction_duration_seconds_bucket:rate5m{
    environment="$environment",
    type="sidekiq",
    stage="$stage"
  }) by (le, job)
)

sum(
  rate(
    gitlab_transaction_rails_queue_duration_total{
      environment="$environment",
      type="sidekiq",
      stage="$stage"}
      [$__interval]
  )
) by (job)

sum(
  rate(
    gitlab_cache_operations_total{
      environment="$environment",
      type="sidekiq",
      stage="$stage"}[$__interval]
  )
) by (job)

sum(
  changes(
    ruby_process_start_time_seconds{
      environment="$environment",
      type="sidekiq",
      stage="$stage",
      job="gitlab-rails"
    }[$__interval]
  )
)

avg(
  avg_over_time(
    db_load_balancing_hosts{
      environment="$environment",
      type="sidekiq",
      stage="$stage"}[$__interval]
  )
) by (job)

clamp_min(
  clamp_max(
    max(
  (
    1 -
    instance:node_filesystem_avail:ratio{type="sidekiq", environment="$environment", stage="$stage", priority=~"$priority" ,fstype=~"ext.|xfs"}
  )
) by (device, fqdn)

  ,1)
,0)

clamp_min(
  clamp_max(
    max(
  label_replace(
    process_open_fds{type="sidekiq", environment="$environment", stage="$stage", priority=~"$priority"}
    /
    process_max_fds{type="sidekiq", environment="$environment", stage="$stage", priority=~"$priority"}
    , "client", "general", "", ""
  )
  or
  label_replace(
    ruby_file_descriptors{type="sidekiq", environment="$environment", stage="$stage", priority=~"$priority"}
    /
    ruby_process_max_fds{type="sidekiq", environment="$environment", stage="$stage", priority=~"$priority"}
    , "client", "ruby", "", ""
  )
) by (job, instance)

  ,1)
,0)
