targets.expr:
sum by (queue) (
  (
    label_replace(
      sidekiq_queue_size{environment="$environment"} and on(fqdn) (redis_connected_slaves != 0),
      "queue", "$0", "name", ".*"
    )
  )
  and on (queue)
  (
    max by (queue) (
      rate(sidekiq_jobs_queue_duration_seconds_sum{environment="$environment", shard=~"$shard"}[$__range]) > 0
    )
  )
)

sum(
  (
    label_replace(
      sidekiq_queue_size{environment="$environment"} and on(fqdn) (redis_connected_slaves != 0),
      "queue", "$0", "name", ".*"
    )
  )
  and on (queue)
  (
    max by (queue) (
      rate(sidekiq_jobs_queue_duration_seconds_sum{environment="$environment", shard=~"$shard"}[$__range]) > 0
    )
  )
