histogram_quantile(0.90,
  sum(job_environment:gitlab_transaction_duration_seconds_bucket:rate5m{
    environment="$environment",
    type="sidekiq",
    stage="$stage"
  }) by (le, job)
)

sum(
  rate(
    gitlab_transaction_rails_queue_duration_total{
      environment="$environment",
      type="sidekiq",
      stage="$stage"}
      [$__interval]
  )
) by (job)

sum(
  rate(
    gitlab_cache_operations_total{
      environment="$environment",
      type="sidekiq",
      stage="$stage"}[$__interval]
  )
) by (job)

sum(
  changes(
    ruby_process_start_time_seconds{
      environment="$environment",
      type="sidekiq",
      stage="$stage",
      job="gitlab-rails"
    }[$__interval]
  )
)

avg(
  avg_over_time(
    db_load_balancing_hosts{
      environment="$environment",
      type="sidekiq",
      stage="$stage"}[$__interval]
  )
) by (job)

clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{type="sidekiq", environment="$environment", stage="$stage", shard=~"$shard"}
        /
        process_max_fds{type="sidekiq", environment="$environment", stage="$stage", shard=~"$shard"}
      )
      or
      (
        ruby_file_descriptors{type="sidekiq", environment="$environment", stage="$stage", shard=~"$shard"}
        /
        ruby_process_max_fds{type="sidekiq", environment="$environment", stage="$stage", shard=~"$shard"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)
