targets.expr:
clamp_min(
  avg by (type) (
    gitlab_service_ops:rate:prediction{env="$environment", environment="$environment", monitor="global", stage="$stage", type="waf"}
    + (2) *
    gitlab_service_ops:rate:stddev_over_time_1w{env="$environment", environment="$environment", monitor="global", stage="$stage", type="waf"}
  )
  or
  (
      sum by (type) (gitlab_service_ops:rate:prediction{environment="$environment", monitor!~"global|", stage="$stage", type="waf"})
      + (2) *
      sum by (type) (gitlab_service_ops:rate:stddev_over_time_1w{environment="$environment", monitor!~"global|", stage="$stage", type="waf"})
  ),
  0
)

clamp_min(
  avg by (type) (
    gitlab_service_ops:rate:prediction{env="$environment", environment="$environment", monitor="global", stage="$stage", type="waf"}
    + (-2) *
    gitlab_service_ops:rate:stddev_over_time_1w{env="$environment", environment="$environment", monitor="global", stage="$stage", type="waf"}
  )
  or
  (
      sum by (type) (gitlab_service_ops:rate:prediction{environment="$environment", monitor!~"global|", stage="$stage", type="waf"})
      + (-2) *
      sum by (type) (gitlab_service_ops:rate:stddev_over_time_1w{environment="$environment", monitor!~"global|", stage="$stage", type="waf"})
  ),
  0
)

sum(
  gitlab_component_errors:rate{component="zone_gitlab_com", environment="$environment", monitor!~"global|", stage="$stage", type="waf"}
) by (component)
/
sum(
  gitlab_component_ops:rate{component="zone_gitlab_com", environment="$environment", monitor!~"global|", stage="$stage", type="waf"}
) by (component)

sum(
  avg_over_time(
    gitlab_component_ops:rate{component="zone_gitlab_com", environment="$environment", monitor!~"global|", stage="$stage", type="waf"}[$__interval]
  )
) by (component)

sum(
  gitlab_component_errors:rate{component="zone_gitlab_net", environment="$environment", monitor!~"global|", stage="$stage", type="waf"}
) by (component)
/
sum(
  gitlab_component_ops:rate{component="zone_gitlab_net", environment="$environment", monitor!~"global|", stage="$stage", type="waf"}
) by (component)

sum(
  avg_over_time(
    gitlab_component_ops:rate{component="zone_gitlab_net", environment="$environment", monitor!~"global|", stage="$stage", type="waf"}[$__interval]
  )
) by (component)

sum(
  gitlab_component_errors:rate{component="zone_staging_gitlab_com", environment="$environment", monitor!~"global|", stage="$stage", type="waf"}
) by (component)
/
sum(
  gitlab_component_ops:rate{component="zone_staging_gitlab_com", environment="$environment", monitor!~"global|", stage="$stage", type="waf"}
) by (component)

sum(
  avg_over_time(
    gitlab_component_ops:rate{component="zone_staging_gitlab_com", environment="$environment", monitor!~"global|", stage="$stage", type="waf"}[$__interval]
  )
) by (component)

max by (type, stage, component) (
  clamp_max(
    gitlab_component_saturation:ratio{environment="$environment", type="waf", stage=~"|$stage"}
    ,
    1
  ) >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:avg_over_time_1w{
        environment="$environment",
        type="waf", stage=~"|$stage"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="waf", stage=~"|$stage"
        }
      , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:predict_linear_2w{
        environment="$environment",
        type="waf", stage=~"|$stage"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="waf", stage=~"|$stage"
        }
    , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

clamp_min(clamp_max(
  max(
    gitlab_component_saturation:ratio{
      type="waf",
      environment="$environment",
      stage=~"|$stage"
    }
  ) by (component)
  ,1)
,0)
