targets.expr:
min(
  min_over_time(
    gitlab_service_apdex:ratio{environment="$environment", type="web", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  max_over_time(
    gitlab_service_errors:ratio{environment="$environment", type="web", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  avg_over_time(
    gitlab_service_ops:rate{environment="$environment", type="web", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

min(
  min_over_time(
    gitlab_component_apdex:ratio{environment="$environment", type="web", stage="$stage", component="puma"}[$__interval]
  )
) by (component)

max(
  gitlab_component_errors:rate{environment="$environment", type="web", stage="$stage", component="puma"}
  /
  gitlab_component_ops:rate{environment="$environment", type="web", stage="$stage", component="puma"}
) by (component)

1 +
max(
  avg_over_time(
    gitlab_component_ops:rate{environment="$environment", type="web", stage="$stage", component="puma"}[$__interval]
  )
) by (component)

min(
  min_over_time(
    gitlab_component_apdex:ratio{environment="$environment", type="web", stage="$stage", component="workhorse"}[$__interval]
  )
) by (component)

max(
  gitlab_component_errors:rate{environment="$environment", type="web", stage="$stage", component="workhorse"}
  /
  gitlab_component_ops:rate{environment="$environment", type="web", stage="$stage", component="workhorse"}
) by (component)

1 +
max(
  avg_over_time(
    gitlab_component_ops:rate{environment="$environment", type="web", stage="$stage", component="workhorse"}[$__interval]
  )
) by (component)

histogram_quantile(
  0.950000,
  sum by (le) (
    rate(http_request_duration_seconds_bucket{job="gitlab-rails", type="web", environment="$environment", stage="$stage"}[$__interval])
  )
)

sum by () (
  changes(http_request_duration_seconds_count{job="gitlab-rails", type="web", status=~"5..", environment="$environment", stage="$stage"}[$__interval])
)

histogram_quantile(
  0.950000,
  sum by (fqdn, le) (
    rate(http_request_duration_seconds_bucket{job="gitlab-rails", type="web", environment="$environment", stage="$stage"}[$__interval])
  )
)

sum by (fqdn) (
  changes(http_request_duration_seconds_count{job="gitlab-rails", type="web", status=~"5..", environment="$environment", stage="$stage"}[$__interval])
)

histogram_quantile(
  0.950000,
  sum by (method, le) (
    rate(http_request_duration_seconds_bucket{job="gitlab-rails", type="web", environment="$environment", stage="$stage"}[$__interval])
  )
)

sum by (method) (
  changes(http_request_duration_seconds_count{job="gitlab-rails", type="web", status=~"5..", environment="$environment", stage="$stage"}[$__interval])
)

sum by () (
  changes(gitlab_workhorse_http_requests_total{job="gitlab-workhorse-web", type="web", code=~"^5.*", route!="^/-/health$", route!="^/-/(readiness|liveness)$", environment="$environment", stage="$stage"}[$__interval])
)

sum by (fqdn) (
  changes(gitlab_workhorse_http_requests_total{job="gitlab-workhorse-web", type="web", code=~"^5.*", route!="^/-/health$", route!="^/-/(readiness|liveness)$", environment="$environment", stage="$stage"}[$__interval])
)

sum by (route) (
  changes(gitlab_workhorse_http_requests_total{job="gitlab-workhorse-web", type="web", code=~"^5.*", route!="^/-/health$", route!="^/-/(readiness|liveness)$", environment="$environment", stage="$stage"}[$__interval])
)

clamp_min(
  clamp_max(
    max(
  (
    1 -
    instance:node_filesystem_avail:ratio{environment="$environment", stage="$stage", type="web" ,fstype=~"ext.|xfs"}
  )
) by (device, fqdn)

  ,1)
,0)

clamp_min(
  clamp_max(
    max(
  label_replace(
    process_open_fds{environment="$environment", stage="$stage", type="web"}
    /
    process_max_fds{environment="$environment", stage="$stage", type="web"}
    , "client", "general", "", ""
  )
  or
  label_replace(
    ruby_file_descriptors{environment="$environment", stage="$stage", type="web"}
    /
    ruby_process_max_fds{environment="$environment", stage="$stage", type="web"}
    , "client", "ruby", "", ""
  )
) by (job, instance)

  ,1)
,0)

clamp_min(
  clamp_max(
    sum by (fqdn, type, tier, stage, environment) (
  go_memstats_alloc_bytes{environment="$environment", stage="$stage", type="web"}
)
/
sum by (fqdn, type, tier, stage, environment) (
  node_memory_MemTotal_bytes{environment="$environment", stage="$stage", type="web"}
)

  ,1)
,0)

histogram_quantile(
  0.5,
  sum(
    job:gitlab_workhorse_http_request_duration_seconds_bucket:rate1m{
      environment="$environment",
      type="web",
      stage="$stage"}
  ) by (le))

label_replace(
  histogram_quantile(
    0.9,
    sum(
      job:gitlab_workhorse_http_request_duration_seconds_bucket:rate1m{
        environment="$environment",
        type="web",
        stage="$stage"}
    ) by (route, le)),
"route", "none", "route", "")

label_replace(
  histogram_quantile(
    0.5,
    sum(
      job:gitlab_workhorse_http_request_duration_seconds_bucket:rate1m{
        environment="$environment",
        type="web",
        stage="$stage"}
    ) by (route, le)),
"route", "none", "route", "")

sum(
  label_replace(
    sum(
      job:gitlab_workhorse_http_request_duration_seconds_bucket:rate1m{
          environment="$environment",
          type="web",
          stage="$stage",
          le="+Inf"}
    ) by (code),
  "code_class", "${1}XX", "code", "(.).*")
) by (code_class)

sum(
  job:gitlab_workhorse_http_request_duration_seconds_bucket:rate1m{
      environment="$environment",
      type="web",
      stage="$stage",
      le="+Inf"}
) by (code)

sum(
  label_replace(
    sum(
      job:gitlab_workhorse_http_request_duration_seconds_bucket:rate1m{
          environment="$environment",
          type="web",
          stage="$stage",
          le="+Inf"}
    ) by (route),
  "route", "none", "route", "")
) by (route)

sum(
  unicorn_active_connections{
    environment="$environment",
    type="web",
    stage="$stage"}
) by (type)

sum(
  unicorn_active_connections{
    environment="$environment",
    type="web",
    stage="$stage"}
) by (fqdn)

sum(
  job:unicorn_queued_connections:sum{
    environment="$environment",
    type="web",
    stage="$stage"}
) by (type)

sum(
  unicorn_queued_connections{
    environment="$environment",
    type="web",
    stage="$stage"}
) by (fqdn)

sum(
  changes(
    unicorn_killer_terminations_total{
      environment="$environment",
      type="web",
      stage="$stage"
    }[$__interval])
) by (type)

sum(
  changes(
    unicorn_killer_terminations_total{
      environment="$environment",
      type="web",
      stage="$stage"
    }[$__interval])
) by (fqdn)

histogram_quantile(0.90,
  sum(job_environment:gitlab_transaction_duration_seconds_bucket:rate5m{
    environment="$environment",
    type="web",
    stage="$stage"
  }) by (le, job)
)

sum(
  rate(
    gitlab_transaction_rails_queue_duration_total{
      environment="$environment",
      type="web",
      stage="$stage"}
      [$__interval]
  )
) by (job)

sum(
  rate(
    gitlab_cache_operations_total{
      environment="$environment",
      type="web",
      stage="$stage"}[$__interval]
  )
) by (job)

sum(
  changes(
    ruby_process_start_time_seconds{
      environment="$environment",
      type="web",
      stage="$stage",
      job="gitlab-rails"
    }[$__interval]
  )
)

avg(
  avg_over_time(
    db_load_balancing_hosts{
      environment="$environment",
      type="web",
      stage="$stage"}[$__interval]
  )
) by (job)

max by (type, stage, component) (
  clamp_max(
    gitlab_component_saturation:ratio{environment="$environment", type="web", stage=~"|$stage"}
    ,
    1
  ) >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:avg_over_time_1w{
        environment="$environment",
        type="web", stage=~"|$stage"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="web", stage=~"|$stage"
        }
      , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

sort_desc(
  max by (type, stage, component) (
    clamp_max(
      gitlab_component_saturation:ratio:predict_linear_2w{
        environment="$environment",
        type="web", stage=~"|$stage"
      } +
      2 *
        gitlab_component_saturation:ratio:stddev_over_time_1w{
          environment="$environment",
          type="web", stage=~"|$stage"
        }
    , 1
    )
    >= on(component, monitor, env, cluster) group_left slo:max:soft:gitlab_component_saturation:ratio
  )
)

clamp_min(clamp_max(
  max(
    gitlab_component_saturation:ratio{
      type="web",
      environment="$environment",
      stage=~"|$stage"
    }
  ) by (component)
  ,1)
,0)
