targets.expr:
histogram_quantile(
  0.5,
  sum(
    job:gitlab_workhorse_http_request_duration_seconds_bucket:rate1m{
      environment="$environment",
      type="web",
      stage="$stage"}
  ) by (le))

label_replace(
  histogram_quantile(
    0.9,
    sum(
      job:gitlab_workhorse_http_request_duration_seconds_bucket:rate1m{
        environment="$environment",
        type="web",
        stage="$stage"}
    ) by (route, le)),
"route", "none", "route", "")

label_replace(
  histogram_quantile(
    0.5,
    sum(
      job:gitlab_workhorse_http_request_duration_seconds_bucket:rate1m{
        environment="$environment",
        type="web",
        stage="$stage"}
    ) by (route, le)),
"route", "none", "route", "")

sum(
  label_replace(
    sum(
      job:gitlab_workhorse_http_request_duration_seconds_bucket:rate1m{
          environment="$environment",
          type="web",
          stage="$stage",
          le="+Inf"}
    ) by (code),
  "code_class", "${1}XX", "code", "(.).*")
) by (code_class)

sum(
  job:gitlab_workhorse_http_request_duration_seconds_bucket:rate1m{
      environment="$environment",
      type="web",
      stage="$stage",
      le="+Inf"}
) by (code)

sum(
  label_replace(
    sum(
      job:gitlab_workhorse_http_request_duration_seconds_bucket:rate1m{
          environment="$environment",
          type="web",
          stage="$stage",
          le="+Inf"}
    ) by (route),
  "route", "none", "route", "")
) by (route)

histogram_quantile(0.90,
  sum(job_environment:gitlab_transaction_duration_seconds_bucket:rate5m{
    environment="$environment",
    type="web",
    stage="$stage"
  }) by (le, job)
)

sum(
  rate(
    gitlab_transaction_rails_queue_duration_total{
      environment="$environment",
      type="web",
      stage="$stage"}
      [$__interval]
  )
) by (job)

sum(
  rate(
    gitlab_cache_operations_total{
      environment="$environment",
      type="web",
      stage="$stage"}[$__interval]
  )
) by (job)

min(
  min_over_time(
    gitlab_service_apdex:ratio{environment="$environment", type="web", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  max_over_time(
    gitlab_service_errors:ratio{environment="$environment", type="web", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

min(
  min_over_time(
    gitlab_service_availability:ratio{environment="$environment", type="web", stage="$stage"}[$__interval]
  )
) by (tier, type)

min(
  min_over_time(
    gitlab_service_availability:ratio{environment="$environment", type="web", stage=""}[$__interval]
  )
) by (tier, type)

min(
  min_over_time(
    gitlab_service_availability:ratio{environment="$environment", type="web", stage="$stage"}[$__interval] offset 1w
  )
) by (tier, type)

max(
  avg_over_time(
    gitlab_service_ops:rate{environment="$environment", type="web", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

max(
  max_over_time(
    gitlab_service_saturation:ratio{environment="$environment", type="web", stage="$stage"}[$__interval] offset 1w
  )
) by (type)

clamp_min(clamp_max(
        1 -
        (
          (
            node_memory_MemFree_bytes{type="web", environment="$environment", stage="$stage"} +
            node_memory_Buffers_bytes{type="web", environment="$environment", stage="$stage"} +
            node_memory_Cached_bytes{type="web", environment="$environment", stage="$stage"}
          )
        )
        /
        node_memory_MemTotal_bytes{type="web", environment="$environment", stage="$stage"}
      ,1),0)

sort(
          clamp_min(
            clamp_max(
              min(
                gitlab_component_saturation:ratio:sapdex:avg_over_time_1w{environment="$environment", type="web", stage=~"|$stage"}
              ) by (component),
            1),
          0)
        )

sort(
          clamp_min(
            clamp_max(
              min(
                gitlab_component_saturation:ratio:sapdex:avg_over_time_1w:predict_linear_30d{environment="$environment", type="web", stage=~"|$stage"}
              ) by (component),
            1),
          0)
        )
