targets.expr:
clamp_min(clamp_max((
  (
    sum by (fqdn) (
  rate(gitlab_pages_http_request_duration_seconds_bucket{env="$environment",environment="$environment",fqdn!="",le="+Inf",stage="$stage",type="web-pages"}[$__interval])
)

    -
    (
      sum by (fqdn) (
  rate(gitlab_pages_http_request_duration_seconds_bucket{env="$environment",environment="$environment",fqdn!="",le="10",stage="$stage",type="web-pages"}[$__interval])
)

    )
  )
  / ignoring(fqdn) group_left()
  (
    sum by () (
  rate(gitlab_pages_http_request_duration_seconds_bucket{env="$environment",environment="$environment",fqdn!="",le="+Inf",stage="$stage",type="web-pages"}[$__interval])
)

  )
) > 0
,1),0)

clamp_min(clamp_max((
  (
    sum by (fqdn) (
  rate(gitlab_pages_http_time_to_write_header_seconds_bucket{env="$environment",environment="$environment",fqdn!="",le="+Inf",stage="$stage",type="web-pages"}[$__interval])
)

    -
    (
      sum by (fqdn) (
  rate(gitlab_pages_http_time_to_write_header_seconds_bucket{env="$environment",environment="$environment",fqdn!="",le="0.5",stage="$stage",type="web-pages"}[$__interval])
)

    )
  )
  / ignoring(fqdn) group_left()
  (
    sum by () (
  rate(gitlab_pages_http_time_to_write_header_seconds_bucket{env="$environment",environment="$environment",fqdn!="",le="+Inf",stage="$stage",type="web-pages"}[$__interval])
)

  )
) > 0
,1),0)

clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, cluster, pod) (
  clamp_min(
    clamp_max(
      (
        go_memstats_alloc_bytes{env="$environment",environment="$environment",stage="$stage",type="web-pages"}
        / on(environment, tier, type, stage, cluster, pod) group_left()
        topk by(environment, tier, type, stage, cluster, pod) (1,
          container_spec_memory_limit_bytes:labeled{container=~"gitlab-pages|gitlab-workhorse|kas|registry|thanos-store",env="$environment",environment="$environment",stage="$stage",type="web-pages"}
        )
      )
      or
      (
        go_memstats_alloc_bytes{type="monitoring", env="$environment",environment="$environment",stage="$stage",type="web-pages"}
        / on(environment, tier, type, cluster, pod) group_left()
        topk by (environment, tier, type, cluster, pod) (1,
          container_spec_memory_limit_bytes:labeled{type="monitoring", container=~"gitlab-pages|gitlab-workhorse|kas|registry|thanos-store"}
        )
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)

clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{env="$environment",environment="$environment",stage="$stage",type="web-pages"}
        /
        process_max_fds{env="$environment",environment="$environment",stage="$stage",type="web-pages"}
      )
      or
      (
        ruby_file_descriptors{env="$environment",environment="$environment",stage="$stage",type="web-pages"}
        /
        ruby_process_max_fds{env="$environment",environment="$environment",stage="$stage",type="web-pages"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)
