targets.expr:
clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{env="$environment",environment="$environment",stage="$stage",type="websockets"}
        /
        process_max_fds{env="$environment",environment="$environment",stage="$stage",type="websockets"}
      )
      or
      (
        ruby_file_descriptors{env="$environment",environment="$environment",stage="$stage",type="websockets"}
        /
        ruby_process_max_fds{env="$environment",environment="$environment",stage="$stage",type="websockets"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)

histogram_quantile(
  0.5,
  sum(
    job:gitlab_workhorse_http_request_duration_seconds_bucket:rate1m{
      environment="$environment",
      type="websockets",
      stage="$stage"}
  ) by (le))

label_replace(
  histogram_quantile(
    0.9,
    sum(
      job:gitlab_workhorse_http_request_duration_seconds_bucket:rate1m{
        environment="$environment",
        type="websockets",
        stage="$stage"}
    ) by (route, le)),
"route", "none", "route", "")

label_replace(
  histogram_quantile(
    0.5,
    sum(
      job:gitlab_workhorse_http_request_duration_seconds_bucket:rate1m{
        environment="$environment",
        type="websockets",
        stage="$stage"}
    ) by (route, le)),
"route", "none", "route", "")

sum(
  label_replace(
    sum(
      job:gitlab_workhorse_http_request_duration_seconds_bucket:rate1m{
          environment="$environment",
          type="websockets",
          stage="$stage",
          le="+Inf"}
    ) by (code),
  "code_class", "${1}XX", "code", "(.).*")
) by (code_class)

sum(
  job:gitlab_workhorse_http_request_duration_seconds_bucket:rate1m{
      environment="$environment",
      type="websockets",
      stage="$stage",
      le="+Inf"}
) by (code)

sum(
  label_replace(
    sum(
      job:gitlab_workhorse_http_request_duration_seconds_bucket:rate1m{
          environment="$environment",
          type="websockets",
          stage="$stage",
          le="+Inf"}
    ) by (route),
  "route", "none", "route", "")
) by (route)

histogram_quantile(0.90,
  sum(job_environment:gitlab_transaction_duration_seconds_bucket:rate5m{
    environment="$environment",
    type="websockets",
    stage="$stage"
  }) by (le, job)
)

sum(
  rate(
    gitlab_transaction_rails_queue_duration_total{
      environment="$environment",
      type="websockets",
      stage="$stage"}
      [$__interval]
  )
) by (job)

sum(
  rate(
    gitlab_cache_operations_total{
      environment="$environment",
      type="websockets",
      stage="$stage"}[$__interval]
  )
) by (job)

sum(
  changes(
    ruby_process_start_time_seconds{
      environment="$environment",
      type="websockets",
      stage="$stage",
      job="gitlab-rails"
    }[$__interval]
  )
)

avg(
  avg_over_time(
    db_load_balancing_hosts{
      environment="$environment",
      type="websockets",
      stage="$stage"}[$__interval]
  )
) by (job)

clamp_min(clamp_max(
  max(
    gitlab_component_saturation:ratio{
      type="websockets",
      environment="$environment",
      stage=~"|$stage"
    }
  ) by (component)
  ,1)
,0)
