targets.expr:
clamp_min(clamp_max((
  sum by (job_name) (
  rate(woodhouse_async_job_duration_seconds_bucket{env="$environment",environment="$environment",job="woodhouse",le="+Inf",stage="$stage"}[$__interval])
)

  -
  (
    sum by (job_name) (
  rate(woodhouse_async_job_duration_seconds_bucket{env="$environment",environment="$environment",job="woodhouse",le="10",stage="$stage"}[$__interval])
)

  )
)
/ ignoring(job_name) group_left()
(
  sum by () (
  rate(woodhouse_async_job_duration_seconds_bucket{env="$environment",environment="$environment",job="woodhouse",le="+Inf",stage="$stage"}[$__interval])
)

)

,1),0)

clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{env="$environment",environment="$environment",stage="$stage",type="woodhouse"}
        /
        process_max_fds{env="$environment",environment="$environment",stage="$stage",type="woodhouse"}
      )
      or
      (
        ruby_file_descriptors{env="$environment",environment="$environment",stage="$stage",type="woodhouse"}
        /
        ruby_process_max_fds{env="$environment",environment="$environment",stage="$stage",type="woodhouse"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)
